import React, { useState, useEffect } from 'react'
import { Container, Navbar, Nav, Button } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import axios from 'axios'

function Header({ logoutCallback }) {
    const [currentUser, setCurrentUser] = useState(JSON.parse(localStorage.getItem('user')))

    const logoutHandler = () => {
        logoutCallback()
    }

    return (
        <Navbar bg="primary" variant="dark">
            <Container>
                <Navbar.Brand href="#home">BasketballWay</Navbar.Brand>
                    <Nav className="mr-auto">
                        { currentUser ? (
                            <>
                                <Nav.Item>Welcome, {currentUser.username}</Nav.Item>
                                <Link to="/scoreboard"><Nav.Item>Scoreboard</Nav.Item></Link>
                            </>
                            ): null 
                        }
                        { currentUser&&currentUser.role===2 ?  (
                            <Link to="team/"><Nav.Item>My Team</Nav.Item></Link>
                        ): null }
                        { currentUser&&currentUser.role===3 ?  (
                            <Link to="teams/"><Nav.Item>Teams</Nav.Item></Link>
                        ): null }
                        { currentUser ? (
                                <Nav.Item><Button variant="secondary" size="sm" onClick={logoutHandler}>Sign Out</Button></Nav.Item>
                        ) : null }
                    </Nav>

            </Container>
        </Navbar>
    )
}

export default Header
