import { Container, Navbar, Nav, Button } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import axios from 'axios'

function Header({ currentuser, logoutCallback }) {
    const [user, setUser] = useState(currentuser)

    const logoutHandler = () => {
        setCurrentUser(null)
        logoutCallback()
    }

    return (
        <Navbar bg="primary" variant="dark">
            <Container>
                <Navbar.Brand href="#home">BasketballWay</Navbar.Brand>
                    <Nav className="me-auto">
                        { currentUser ? (
                            <>
                                <Nav.Item>{user.username}</Nav.Item>
                                <Nav.Item>Scoreboard</Nav.Item>
                            </>
                            ): null 
                        }
                        { user&&user.role===2 ?  (
                            <Nav.Item>My Team</Nav.Item>
                        ): null }
                        { user&&user.role===3 ?  (
                            <Nav.Item>Teams</Nav.Item>
                        ): null }
                        { user ? (
                                <Nav.Item><Button variant="secondary" size="sm" onClick={logoutHandler}>Sign Out</Button></Nav.Item>
                        ) : null }
                    </Nav>

            </Container>
        </Navbar>
    )
}

export default Header
